import React, { useState } from 'react';
import Swal from 'sweetalert2';

const PDFExtractor = () => {
  const [file, setFile] = useState(null);
  const [totalPages, setTotalPages] = useState(0);
  const [selectedPages, setSelectedPages] = useState([]);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
  };

  const handleUpload = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a PDF file.');
      return;
    }

    const formData = new FormData();
    formData.append('pdfFile', file); // Append the file with key 'pdfFile'

    try {
      const response = await fetch('/upload', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Failed to upload PDF.');
      }

      Swal.fire('PDF uploaded successfully.');
      const fileName = file.name;
      fetchPdfDetails(fileName);
    } catch (error) {
      console.error('Upload error:', error);
      Swal.fire('Failed to upload PDF.');
    }
  };

  const fetchPdfDetails = async (fileName) => {
    try {
      const response = await fetch(`/pdf-details/${fileName}`);
      if (!response.ok) {
        throw new Error('Failed to fetch PDF details.');
      }
      const data = await response.json();
      setTotalPages(data.totalPages);
    } catch (error) {
      console.error('PDF details fetch error:', error);
      Swal.fire('Failed to fetch PDF details.');
    }
  };

  return (
    <div>
      <form id="pdfUploadForm" enctype="multipart/form-data" onSubmit={handleUpload}>
        <input type="file" id="pdfFile" accept=".pdf" required onChange={handleFileChange} />
        <button type="submit">Upload PDF</button>
      </form>
      {totalPages > 0 && (
        <>
          <p>Total Pages: {totalPages}</p>
          {/* Add checkboxes and extraction button here */}
        </>
      )}
    </div>
  );
};

export default PDFExtractor;
