import React, { useState } from 'react';
import Swal from 'sweetalert2';

const PDFExtractor = () => {
  const [file, setFile] = useState(null);
  const [totalPages, setTotalPages] = useState(0);
  const [selectedPages, setSelectedPages] = useState([]);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
    if (selectedFile) {
      fetchPdfDetails(selectedFile.name);
    }
  };

  const fetchPdfDetails = (fileName) => {
    fetch(`/pdf-details/${fileName}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch PDF details.');
        }
        return response.json();
      })
      .then(data => {
        setTotalPages(data.totalPages);
      })
      .catch(error => {
        console.error('PDF details fetch error:', error);
        alert('Failed to fetch PDF details.');
      });
  };

  const handleCheckboxChange = (event) => {
    const selectedPage = parseInt(event.target.value);
    if (event.target.checked) {
      setSelectedPages([...selectedPages, selectedPage]);
    } else {
      setSelectedPages(selectedPages.filter(page => page !== selectedPage));
    }
  };

  const handlePDFExtraction = () => {
    if (selectedPages.length > 0) {
      const fileName = file.name;
      fetch('/extract', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ selectedPages: selectedPages.join(','), fileName: fileName })
      })
        .then(response => {
          if (response.ok) {
            return response.blob();
          } else {
            throw new Error('Failed to extract PDF.');
          }
        })
        .then(blob => {
          const url = window.URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          a.download = fileName;
          document.body.appendChild(a);
          a.click();
          window.URL.revokeObjectURL(url);
          a.remove();
          Swal.fire({
            icon: 'success',
            title: 'Success!',
            text: 'PDF Extracted successfully.'
          });
        })
        .catch(error => {
          console.error('Extraction error:', error);
          alert('Failed to extract PDF.');
        });
    } else {
      alert('Please select at least one page.');
    }
  };

  return (
    <div>
      <form id="pdfUploadForm" onSubmit={(event) => event.preventDefault()}>
        <input type="file" id="pdfFile" onChange={handleFileChange} accept=".pdf" />
      </form>
      <div>Total Pages: {totalPages}</div>
      <div>
        {Array.from({ length: totalPages }, (_, index) => index + 1).map((pageNumber) => (
          <div key={pageNumber}>
            <input
              type="checkbox"
              id={`page${pageNumber}`}
              value={pageNumber}
              onChange={handleCheckboxChange}
            />
            <label htmlFor={`page${pageNumber}`}>Page {pageNumber}</label>
          </div>
        ))}
      </div>
      <button id="extractPagesBtn" onClick={handlePDFExtraction}>Extract Pages</button>
    </div>
  );
};

export default PDFExtractor;
